mapper.delete:
delete
from t_like
where post_id = #{param1}
  and user_id = #{param2}

mapper.select:
select user_id
from t_like
where post_id = #{postid}

select count(*)
from t_like
where post_id = #{param1}
  and user_id = #{param2}

mapper.delete:
delete
from t_comment_like
where user_id = #{param1}
  and comment_id = #{param2}

mapper.select:
select count(*)
from t_comment_like
